
public class DeclaracionOperaciones2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double apulgadas = 2.54; // esta es una constante 
		double cm = 6;
		double resultado = cm/apulgadas;
		System.out.println("En "+ 6 + "cm hay " + resultado +" pulgadas" );
	}

}
